    srv.setCurrentLanguage = function(newCurrentLanguage) {
        currentLanguage = newCurrentLanguage;
        help = [];
        var tmp = srv._translate[0][currentLanguage];
        var orderBy = $filter('orderBy');

        angular.forEach(tmp, function(value, key) {
            if (   key.substring(0,4) != "help"
                && key.substring(0,2) != "hh"
                && (["id","zinsen","leavepage","analyse","inputpercent","input","ueberschuss","zahlungen","inputprice","inputpercent","jahre","remark","required"].indexOf(key)  < 0)
                )  {
                help.push({"name":value,"transid":key});
            }
        });
        help = orderBy(help,'-name',true);
    };
    srv.getCurrentLanguage = function() {
        return currentLanguage;
    };
    srv.getTrans = function(label) {
        return srv._translate[0][currentLanguage][label];
    };
    srv.getHelp = function() {
        return help;
    };
    srv.getLanguages = function() {
        var lang = [];
        var tmp = srv._translate[0];
        angular.forEach(tmp, function (value,key) {
           lang.push({"id":key});
        });
        return lang;
    };


